$(document).ready(function() {

    $('#submit').click(function() {

        event.preventDefault(); // prevent PageReLoad

		var validUser = $('#username').val() === 'admin'; // User validate
		var validPassword = $('#password').val() === 'admin'; // Password validate

        if (validUser === true && validPassword === true) { 
            window.location = "https://sharadpatil99.github.io/sharadpatil99/home.html"; // go to home.html
        }
        else {
            alert('Please enter correct Username & Password');
        }
    });
});